    {
        int list_1 [] = {7,9,10,5,8,4,2,1,6,3,7,9,10,5,8,4,2};
        int num = 0;
        for (int i = 0;i<17;i++) { num += Integer.parseInt(str_1.substring(i,i+1)) * list_1[i]; } //substring是前闭后开的
        int fin_num = (12-(num%11))%11;
        str_1 = str_1.toLowerCase();  //防止输入的x是大写不便于比对，将其转为小写
        String finally_place = fin_num==10 ? "x" : Integer.toString(fin_num);
        if (str_1.substring(17,18).equals(finally_place)) return true;   //A.equals(B)比较两个String是否相等
        else return false;
    }

    static boolean isInterger(String str_1, int i, int j) //判断某个区间内是否为纯数字
    {
        Pattern pattern = Pattern.compile("\\d+");
        if (pattern.matcher(str_1.substring(i,j)).matches()) return true;
        else return false;
    }

